import './App.css';
import {useState} from 'react';
function App() {
  const [form, setForm] = useState({
    amount:0,
    description:"",
    date:null
  })
  function handleSubmit(){
    console.log("fine");
  }

  function handleInput(e){
    setForm({...form,amount:e.target.value})

  }


  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="number" value={form.amount} onChange={handleInput} placeholder="Enter Transaction amount"/>
        <input type="description" placeholde="enter the trasaction details"/>
        <input type="date"/>
        <button type="submit">Submit</button>
      </form>

    </div>
  );
}

export default App;
